import { USER_DETAILS_SELLER_REQUEST, USER_DETAILS_SELLER_SUCCESS, USER_DETAILS_SELLER_FAIL, SELLER_CONFIRMATION_FAIL, SELLER_CONFIRMATION_REQUEST, SELLER_CONFIRMATION_SUCCESS, SELLER_REGISTER_REQUEST, USER_DETAILS_FAIL, USER_DETAILS_REQUEST, USER_DETAILS_RESET, USER_DETAILS_SUCCESS, USER_LIST_RESET, USER_LIST_FAIL, USER_LIST_REQUEST, USER_LIST_SUCCESS, USER_LOGIN_FAIL, USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS, USER_LOGOUT, USER_REGISTER_FAIL, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_UPDATE_PROFILE_FAIL, USER_UPDATE_PROFILE_REQUEST, USER_UPDATE_PROFILE_SUCCESS, USER_DELETE_FAIL, USER_DELETE_SUCCESS, USER_DELETE_REQUEST, USER_UPDATE_FAIL, USER_UPDATE_SUCCESS, USER_UPDATE_REQUEST, SELLER_REGISTER_FAIL, SELLER_REGISTER_SUCCESS } from "../constants/userConstants"
import axios from 'axios'
import { ORDER_LIST_MY_RESET } from "../constants/orderConstants"

export const login = (email, password ) => async (dispatch) => {
    try {
        dispatch({
            type: USER_LOGIN_REQUEST
        })

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const { data } = await axios.post('/api/users/login', {  email, password }, config)

        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload: data
        })

        localStorage.setItem('userInfo', JSON.stringify(data) )

    } catch(error){
        dispatch({
                        type: USER_LOGIN_FAIL,
                        payload: error.response && error.response.data.message ? 
                        error.response.data.message
                        : error.message
                    })
    }


}


export const logout = () => (dispatch) => {
        localStorage.removeItem('userInfo')
        dispatch({type: USER_LOGOUT})
        dispatch({type: USER_DETAILS_RESET})
        dispatch({type: ORDER_LIST_MY_RESET})
        dispatch({type: USER_LIST_RESET})
    }
export const register = (name, email, password ) => async (dispatch) => {
    try {
        dispatch({
            type: USER_REGISTER_REQUEST
        })

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const { data } = await axios.post('/api/users', { name, email, password }, config)

        dispatch({
            type: USER_REGISTER_SUCCESS,
            payload: data
        })

         dispatch({

            type: USER_LOGIN_SUCCESS,
            payload: data
        })

        localStorage.setItem('userInfo', JSON.stringify(data) )

    } catch(error){
        dispatch({
                        type: USER_REGISTER_FAIL,
                        payload: error.response && error.response.data.message ? 
                        error.response.data.message
                        : error.message
                    })
    }

}


export const saveSellerRegister = ( sellerRegister) => async (dispatch, getState) => {


    console.log('USER2',  sellerRegister)


  try {
    dispatch({
      type: SELLER_REGISTER_REQUEST,
    })

    const {
      userLogin: { userInfo },
    } = getState()

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${userInfo.token}`,
      },
    }

    const { data } = await axios.put(`/api/users/sellerRegister`, sellerRegister,  config)


    console.log('USER REGISTER ACTION', data)

    //console.log('USER4', sellerRegister,)

    dispatch({
      type: SELLER_REGISTER_SUCCESS,
      payload: data,
    })

  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message
    if (message === 'Not authorized, token failed') {
      dispatch(logout())
    }
    dispatch({
      type: SELLER_REGISTER_FAIL,
      payload: message,
    })
  }
}




export const sellerConfirmationPost = (userId, sellerConfirmation) => async (dispatch, getState) => {

    console.log('SELLER1', sellerConfirmation)



  try {
    dispatch({
      type: SELLER_CONFIRMATION_REQUEST,
    })

    const {
      userLogin: { userInfo },
    } = getState()

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${userInfo.token}`,
      },
    }



    const { data } = await axios.put(`/api/users/sellerConfirm/${userId}`, sellerConfirmation, config)

    console.log('ORDER3', data)

    console.log('ORDER4', sellerConfirmation,)

    dispatch({
      type: SELLER_CONFIRMATION_SUCCESS,
      payload: data,
    })

  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message
    if (message === 'Not authorized, token failed') {
      dispatch(logout())
    }
    dispatch({
      type: SELLER_CONFIRMATION_FAIL,
      payload: message,
    })
  }
}




