import React, {useEffect} from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import { Table, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { listUsers, deleteUser } from '../actions/userActions'


const UserListScreen = ({ history }) => {

    const dispatch = useDispatch()

    const userList = useSelector((state) => state.userList)
    const { loading, error, users } = userList

    const userLogin = useSelector((state) => state.userLogin)
    const { userInfo} = userLogin

    const userDelete = useSelector((state) => state.userDelete)
    const { success: successDelete } = userDelete

    useEffect(() => {
        if(userInfo && userInfo.isAdmin) {
            dispatch(listUsers())
        } else {
            history.push('/login')
        }
    }, [dispatch, history, userInfo, successDelete])


    console.log('USERS',users)

    const deleteHandler = (id) => {
        if(window.confirm('Are you sure')) {
            dispatch(deleteUser(id))
        }

    }

    return (
         <>
      <h1>Users</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Table striped bordered hover responsive className='table-sm'>
          <thead>
            <tr>
              <th>ID</th>
              <th>NAME</th>
              <th>EMAIL</th>
              <th>SELLER REGISTRATION DATE</th>
              <th>SELLER CONFIRMED</th>
              <th>CONFIRMED BY</th>
              <th>SELLER CONFIRMATION DATE</th>
              <th>ADMIN</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user._id}</td>
                <td>{user.name}</td>
                <td>
                  <a href={`mailto:${user.email}`}>{user.email}</a>
                </td>
                <td className="text-center">
                  {user && user.sellerRegisteredAt ? (
                    user.sellerRegisteredAt.substring(0, 10) 
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}
                 </td>
                <td className="text-center">
                  {user && user.isSeller ? (
                    <i className='fas fa-check' style={{ color: 'green' }}></i>
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}
                  <LinkContainer to={`/admin/user/${user._id}/sellerConfirmation`}>
                    <Button variant='light' className='btn-sm m-3'>
                      <i className='fas fa-edit'></i>
                    </Button>
                  </LinkContainer>
                </td>

                <td className="text-center">
                  { user && user.isSeller && user.sellerConfirmation ? (
                    user.sellerConfirmation.name
                  ):(
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )} 
                </td>
                <td className="text-center">
                  { user && user.isSeller && user.sellerConfirmation ? (
                    user.sellerConfirmedAt.substring(0, 10) 
                  ):(
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )} 

                </td>
                <td className="text-center"> 
                  {user.isAdmin ? (
                    <i className='fas fa-check' style={{ color: 'green' }}></i>
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}

                </td>

                <td>
                  <LinkContainer to={`/admin/user/${user._id}/edit`}>
                    <Button variant='light' className='btn-sm'>
                      <i className='fas fa-edit'></i>
                    </Button>
                  </LinkContainer>
                  <Button
                    variant='danger'
                    className='btn-sm'
                    onClick={() => deleteHandler(user._id)}
                  >
                    <i className='fas fa-trash'></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </>
    )
}

export default UserListScreen
